import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import styled from "styled-components";
import { FaEdit, FaTrash } from "react-icons/fa"; // Import icon chỉnh sửa và xóa

// Styled components (như trong phiên bản trước)...

const QuizDetail = () => {
  const { quizId } = useParams();
  const [quiz, setQuiz] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [text, setText] = useState("");
  const [options, setOptions] = useState(["", "", "", ""]);
  const [correctAnswerIndex, setCorrectAnswerIndex] = useState(0);
  const [editingQuestion, setEditingQuestion] = useState(null);

  useEffect(() => {
    const fetchQuizDetails = async () => {
      try {
        const quizResponse = await axios.get(
          `http://localhost:3001/quizzes/${quizId}`
        );
        setQuiz(quizResponse.data.data.quiz);

        const questionResponse = await axios.get(
          `http://localhost:3001/quizzes/${quizId}/populate`
        );
        setQuestions(questionResponse.data.data.quiz.questions);
      } catch (error) {
        console.error("Error fetching quiz details:", error);
      }
    };

    fetchQuizDetails();
  }, [quizId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const newQuestion = { text, options, correctAnswerIndex };

      if (editingQuestion) {
        // Update question
        await axios.patch(
          `http://localhost:3001/quizzes/${quizId}/questions/${editingQuestion._id}`,
          newQuestion
        );
        setEditingQuestion(null);
      } else {
        // Create new question
        await axios.post(
          `http://localhost:3001/quizzes/${quizId}/questions`,
          newQuestion
        );
      }

      setText("");
      setOptions(["", "", "", ""]);
      setCorrectAnswerIndex(0);
      const response = await axios.get(
        `http://localhost:3001/quizzes/${quizId}/populate`
      );
      setQuestions(response.data.data.quiz.questions);
    } catch (error) {
      console.error("Error creating or updating question:", error);
    }
  };

  const handleEdit = (question) => {
    setEditingQuestion(question);
    setText(question.text);
    setOptions(question.options);
    setCorrectAnswerIndex(question.correctAnswerIndex);
  };

  const handleDelete = async (questionId) => {
    try {
      await axios.delete(
        `http://localhost:3001/quizzes/${quizId}/questions/${questionId}`
      );
      setQuestions(questions.filter((question) => question._id !== questionId));
    } catch (error) {
      console.error("Error deleting question:", error);
    }
  };

  const handleOptionChange = (index, value) => {
    const newOptions = [...options];
    newOptions[index] = value;
    setOptions(newOptions);
  };

  return (
    <Container>
      {quiz ? (
        <>
          <Title>{quiz.title}</Title>
          <Description>{quiz.description}</Description>
          <QuestionsTitle>Questions</QuestionsTitle>
          <Form onSubmit={handleSubmit}>
            <Input
              type="text"
              placeholder="Question Text"
              value={text}
              onChange={(e) => setText(e.target.value)}
              required
            />
            {options.map((option, index) => (
              <Input
                key={index}
                type="text"
                placeholder={`Option ${index + 1}`}
                value={option}
                onChange={(e) => handleOptionChange(index, e.target.value)}
                required
              />
            ))}
            <Input
              type="number"
              placeholder={`Correct Answer Index (0-${options.length - 1})`}
              value={correctAnswerIndex}
              min="0"
              max={options.length - 1}
              onChange={(e) => setCorrectAnswerIndex(Number(e.target.value))}
              required
            />
            <Button type="submit">
              {editingQuestion ? "Update Question" : "Create New Question"}
            </Button>
          </Form>
          <QuestionList>
            {questions.length > 0 ? (
              questions.map((question) => (
                <QuestionItem key={question._id}>
                  <div>
                    <QuestionText>{question.text}</QuestionText>
                    <OptionList>
                      {question.options.map((option, index) => (
                        <OptionItem
                          key={index}
                          isCorrect={index === question.correctAnswerIndex}
                        >
                          {option}{" "}
                          {index === question.correctAnswerIndex &&
                            "(Correct Answer)"}
                        </OptionItem>
                      ))}
                    </OptionList>
                  </div>
                  <div>
                    <EditIcon onClick={() => handleEdit(question)} />
                    <DeleteIcon onClick={() => handleDelete(question._id)} />
                  </div>
                </QuestionItem>
              ))
            ) : (
              <p>No questions available for this quiz.</p>
            )}
          </QuestionList>
        </>
      ) : (
        <p>Loading...</p>
      )}
    </Container>
  );
};

export default QuizDetail;
