import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import styled from "styled-components";
import { FaEdit, FaTrash } from "react-icons/fa"; // Import icon chỉnh sửa và xóa

// Styled components
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 2rem;
  background-color: #f4f4f4;
  min-height: 100vh;
`;

const Title = styled.h1`
  font-size: 2.5rem;
  color: #333;
  margin-bottom: 1rem;
`;

const Description = styled.p`
  font-size: 1.2rem;
  color: #555;
  margin-bottom: 2rem;
  text-align: center;
`;

const QuestionsTitle = styled.h2`
  font-size: 2rem;
  color: #333;
  margin-bottom: 1rem;
`;

const QuestionList = styled.ul`
  list-style-type: none;
  padding: 0;
  width: 100%;
  max-width: 800px;
`;

const QuestionItem = styled.li`
  background-color: #fff;
  border-radius: 8px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  margin: 1rem 0;
  padding: 1rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
  transition: transform 0.2s;

  &:hover {
    transform: translateY(-4px);
  }
`;

const QuestionText = styled.p`
  font-size: 1.2rem;
  font-weight: bold;
  color: #333;
`;

const OptionList = styled.ul`
  list-style-type: none;
  padding-left: 1.5rem;
`;

const OptionItem = styled.li`
  margin: 0.5rem 0;
  font-size: 1rem;
  color: ${(props) => (props.isCorrect ? "#28a745" : "#555")};
  font-weight: ${(props) => (props.isCorrect ? "bold" : "normal")};
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-bottom: 2rem;
  width: 100%;
  max-width: 400px;
`;

const Input = styled.input`
  padding: 0.5rem;
  margin: 0.5rem 0;
  width: 100%;
  border: 1px solid #ccc;
  border-radius: 4px;
`;

const Button = styled.button`
  padding: 0.5rem 1rem;
  background-color: #007bff;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #0056b3;
  }
`;

const EditIcon = styled(FaEdit)`
  cursor: pointer;
  color: #007bff;
  margin-left: 1rem;

  &:hover {
    color: #0056b3;
  }
`;

const DeleteIcon = styled(FaTrash)`
  cursor: pointer;
  color: #dc3545;
  margin-left: 1rem;

  &:hover {
    color: #b02a37;
  }
`;

const QuizDetail = () => {
  const { quizId } = useParams();
  const [quiz, setQuiz] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [text, setText] = useState("");
  const [options, setOptions] = useState(["", "", "", ""]);
  const [correctAnswerIndex, setCorrectAnswerIndex] = useState(0);
  const [editingQuestion, setEditingQuestion] = useState(null);

  useEffect(() => {
    const fetchQuizDetails = async () => {
      try {
        const quizResponse = await axios.get(
          `http://localhost:3001/quizzes/${quizId}`
        );
        setQuiz(quizResponse.data.data.quiz);

        const questionResponse = await axios.get(
          `http://localhost:3001/quizzes/${quizId}/populate`
        );
        setQuestions(questionResponse.data.data.quiz.questions);
      } catch (error) {
        console.error("Error fetching quiz details:", error);
      }
    };

    fetchQuizDetails();
  }, [quizId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const newQuestion = { text, options, correctAnswerIndex };

      if (editingQuestion) {
        // Update question
        await axios.patch(
          `http://localhost:3001/quizzes/${quizId}/questions/${editingQuestion._id}`,
          newQuestion
        );
        setEditingQuestion(null);
      } else {
        // Create new question
        await axios.post(
          `http://localhost:3001/quizzes/${quizId}/questions`,
          newQuestion
        );
      }

      setText("");
      setOptions(["", "", "", ""]);
      setCorrectAnswerIndex(0);
      const response = await axios.get(
        `http://localhost:3001/quizzes/${quizId}/populate`
      );
      setQuestions(response.data.data.quiz.questions);
    } catch (error) {
      console.error("Error creating or updating question:", error);
    }
  };

  const handleEdit = (question) => {
    setEditingQuestion(question);
    setText(question.text);
    setOptions(question.options);
    setCorrectAnswerIndex(question.correctAnswerIndex);
  };

  const handleDelete = async (questionId) => {
    try {
      await axios.delete(
        `http://localhost:3001/quizzes/${quizId}/questions/${questionId}`
      );
      setQuestions(questions.filter((question) => question._id !== questionId));
    } catch (error) {
      console.error("Error deleting question:", error);
    }
  };

  const handleOptionChange = (index, value) => {
    const newOptions = [...options];
    newOptions[index] = value;
    setOptions(newOptions);
  };

  return (
    <Container>
      {quiz ? (
        <>
          <Title>{quiz.title}</Title>
          <Description>{quiz.description}</Description>
          <QuestionsTitle>Questions</QuestionsTitle>
          <Form onSubmit={handleSubmit}>
            <Input
              type="text"
              placeholder="Question Text"
              value={text}
              onChange={(e) => setText(e.target.value)}
              required
            />
            {options.map((option, index) => (
              <Input
                key={index}
                type="text"
                placeholder={`Option ${index + 1}`}
                value={option}
                onChange={(e) => handleOptionChange(index, e.target.value)}
                required
              />
            ))}
            <Input
              type="number"
              placeholder={`Correct Answer Index (0-${options.length - 1})`}
              value={correctAnswerIndex}
              min="0"
              max={options.length - 1}
              onChange={(e) => setCorrectAnswerIndex(Number(e.target.value))}
              required
            />
            <Button type="submit">
              {editingQuestion ? "Update Question" : "Create New Question"}
            </Button>
          </Form>
          <QuestionList>
            {questions.length > 0 ? (
              questions.map((question) => (
                <QuestionItem key={question._id}>
                  <div>
                    <QuestionText>{question.text}</QuestionText>
                    <OptionList>
                      {question.options.map((option, index) => (
                        <OptionItem
                          key={index}
                          isCorrect={index === question.correctAnswerIndex}
                        >
                          {option}{" "}
                          {index === question.correctAnswerIndex &&
                            "(Correct Answer)"}
                        </OptionItem>
                      ))}
                    </OptionList>
                  </div>
                  <div>
                    <EditIcon onClick={() => handleEdit(question)} />
                    <DeleteIcon onClick={() => handleDelete(question._id)} />
                  </div>
                </QuestionItem>
              ))
            ) : (
              <p>No questions available for this quiz.</p>
            )}
          </QuestionList>
        </>
      ) : (
        <p>Loading...</p>
      )}
    </Container>
  );
};

export default QuizDetail;
