import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import styled from "styled-components";
import Header from "../components/Header"; // Import Header

// Styled components
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 2rem;
  background-color: #f0f0f0;
  min-height: 100vh;
`;

const Title = styled.h1`
  font-size: 2.5rem;
  color: #333;
  margin-bottom: 2rem;
`;

const QuizList = styled.ul`
  list-style-type: none;
  padding: 0;
  width: 100%;
  max-width: 600px;
`;

const QuizItem = styled.li`
  margin: 1rem 0;
  background-color: #fff;
  border-radius: 8px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  transition: transform 0.2s;

  &:hover {
    transform: translateY(-4px);
  }

  a {
    display: block;
    padding: 1rem;
    color: #007bff;
    text-decoration: none;
    font-weight: bold;
  }

  a:hover {
    color: #0056b3;
  }
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-bottom: 2rem;
  width: 100%;
  max-width: 400px;
`;

const Input = styled.input`
  padding: 0.5rem;
  margin: 0.5rem 0;
  width: 100%;
  border: 1px solid #ccc;
  border-radius: 4px;
`;

const Button = styled.button`
  padding: 0.5rem 1rem;
  background-color: #007bff;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #0056b3;
  }
`;

const Quiz = () => {
  const [quizzes, setQuizzes] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");

  useEffect(() => {
    const fetchQuizzes = async () => {
      const response = await axios.get("http://localhost:3001/quizzes");
      setQuizzes(response.data.data.quizzes);
    };
    fetchQuizzes();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const newQuiz = { title, description };
      await axios.post("http://localhost:3001/quizzes", newQuiz);
      setTitle(""); // Reset form fields
      setDescription("");
      // Fetch the updated list of quizzes
      const response = await axios.get("http://localhost:3001/quizzes");
      setQuizzes(response.data.data.quizzes);
    } catch (error) {
      console.error("Error creating new quiz:", error);
    }
  };

  return (
    <Container>
      <Header /> {/* Thêm Header vào đây */}
      <Title>Quiz Page</Title>
      <Form onSubmit={handleSubmit}>
        <Input
          type="text"
          placeholder="Quiz Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <Input
          type="text"
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />
        <Button type="submit">Create New Quiz</Button>
      </Form>
      <QuizList>
        {quizzes.map((quiz) => (
          <QuizItem key={quiz._id}>
            <Link to={`/quizzes/${quiz._id}`}>{quiz.title}</Link>
          </QuizItem>
        ))}
      </QuizList>
    </Container>
  );
};

export default Quiz;
